import { Component, Input } from '@angular/core';
import CustomerRegistrationRequest from 'src/app/models/CustomerRegistrationRequest';
@Component({
  selector: 'app-manage-customer',
  templateUrl: './manage-customer.component.html',
  styleUrls: ['./manage-customer.component.scss']
})
export class ManageCustomerComponent {
  @Input()
  customer:CustomerRegistrationRequest={};


  isVisible: boolean=true;

  @Input()
  visible(): boolean
  {
    if(this.customer.age != null || this.customer.gender!=null || this.customer.name!=null || this.customer.password!=null || this.customer.email!=null )
    {
      return this.isVisible;
    }

    return false;
  }
}
