import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import CustomerRegistrationRequest from 'src/app/models/CustomerRegistrationRequest';
@Component({
  selector: 'app-manage-customer',
  templateUrl: './manage-customer.component.html',
  styleUrls: ['./manage-customer.component.scss']
})
export class ManageCustomerComponent {
  @Input()
  customer:CustomerRegistrationRequest={};

  isVisible: boolean=true;

  @Input()
  operation: 'create' | 'update'='create';

  isValid():boolean
  {
    return this.minLength(this.customer.name) &&
           this.minLength(this.customer.email) &&
           this.minLength(this.customer.password) &&
           this.customer.age !== undefined && this.customer.age > 0 &&
           (this.operation==='update' || this.minLength(this.customer.gender));
  }

  minLength(input:string | undefined): boolean
  {
    return input!==null && input!==undefined && input.length > 0
  }

  @Output()
  submit: EventEmitter<CustomerRegistrationRequest> = new EventEmitter<CustomerRegistrationRequest>;

  onSubmit()
  {
    this.submit.emit(this.customer);
  }


}
