import { Component, Input } from '@angular/core';
import CustomerDTO from '../customer/CustomerDTO';

@Component({
  selector: 'app-customer-card',
  templateUrl: './customer-card.component.html',
  styleUrls: ['./customer-card.component.scss']
})
export class CustomerCardComponent 
{
  @Input()
  customer: CustomerDTO = {};
  @Input()
  customerIndex = 0;
}
<p-card [header]="name" [subheader]="email" [style]="{ width: '360px' }">
    <ng-template pTemplate="header">
        <img alt="Card" src="https://primefaces.org/cdn/primeng/images/usercard.png" />
    </ng-template>
    <span>{{age+" | "+gender}}</span>
    <ng-template pTemplate="footer">
        <p-button label="Update" icon="pi pi-check"></p-button>
        <p-button label="Delete" icon="pi pi-times" styleClass="p-button-secondary" [style]="{ 'margin-left': '.5em' }"></p-button>
    </ng-template>
</p-card>
