import { Component, EventEmitter, Input, Output } from '@angular/core';
import { MyCalculatorService } from '../services/my-calculator.service';

@Component({
  selector: 'app-my-first-component',
  templateUrl: './my-first-component.html',
  styleUrls: ['./my-first-component.scss']
})
export class MyFirstComponent {
  inputValue1:Number=0;
  inputValue2:Number=0;
  result: Number=0;

  constructor(private readonly calculator:MyCalculatorService){}

  addition(): Number{
    return this.calculator.addition(this.inputValue1,this.inputValue2); 
  }

  subtraction(): Number{
    return this.calculator.subtraction(this.inputValue1,this.inputValue2); 
  }

  division(): Number{
    return this.calculator.division(this.inputValue1,this.inputValue2); 
  }

  multiplication(): Number{
    return this.calculator.multiplication(this.inputValue1,this.inputValue2); 
 }
}
