import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Observable } from 'rxjs';
import CustomerDTO from 'src/app/components/customer/CustomerDTO';

@Injectable({
  providedIn: 'root'
})
export class CustomerService {

  constructor(private http: HttpClient) { }

  customers: Array<CustomerDTO>=[];

  getCustomers():Observable<CustomerDTO[]> // we did not define the type within the request
  {
    let jwtToken =localStorage.getItem("token");
    console.log("this is your token ",jwtToken);
    let headers = new HttpHeaders();


      headers.set('Authorization', 'Bearer '+jwtToken);
      console.log("headers: ",headers.get("Authorization"));
      return this.http
      .get<CustomerDTO[]>("http://localhost:8085/api/v1/customers", {'headers': headers})

  }

}
