import { Component, Input, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import CustomerDTO from './CustomerDTO';
import { CustomerService } from 'src/app/services/customer/customer.service';
import CustomerRegistrationRequest from 'src/app/models/CustomerRegistrationRequest';
import { MessageService } from 'primeng/api';
import CustomerUpdateRequest from 'src/app/models/CustomerUpdateRequest';

@Component({
  selector: 'app-customer',
  templateUrl: './customer.component.html',
  styleUrls: ['./customer.component.scss']
})
export class CustomerComponent implements OnInit {

  isVisible: boolean = false;
  isVisible2: boolean = false;

  customer: CustomerRegistrationRequest = {};

  customers: Array<CustomerDTO> = [];

  @Input()
  private customerId: number=0;

  constructor(private customerService: CustomerService, private messageService: MessageService){};

  ngOnInit(): void {
    this.getCustomers();
  }

  private getCustomers(): void {
      this.customerService.getCustomers()
        .subscribe(
          {
            next: (data: CustomerDTO[])=>
            {

              this.customers=data;
              console.log(this.customers);
            }
          })
  }

  private showSuccess(msg: string) 
  {
    this.messageService.add({ severity: 'success', summary: 'Success', detail: msg });
  }

  private showError(msg: string) 
  {
    this.messageService.add({ severity: 'error', summary: 'Error', detail: msg });
  }

  saveCustomer(request: CustomerRegistrationRequest): void
  {
    this.customerService.saveCustomer(request)
    .subscribe({
      //use next if you want to do something after the request was made or need data from request
      next: ()=>
      {
        this.getCustomers();
        this.isVisible=false;
        this.showSuccess("Customer: "+this.customer.name+" has been saved");
        this.customer={};


      },

      error:(err)=>
      {
        this.showError("Customer has not been saved")
      }
    })

  }

  deleteCustomer(id: number):void
  {
    if(id)
    {
      this.customerService.deleteCustomer(id)
      .subscribe({
          next: ()=>
          {
            this.getCustomers();
          }
      });

    }
  }


  updateCustomer(id:number,req:CustomerUpdateRequest):void
  {

    if(id !==undefined)
    {
      this.customerService.updateCustomer(id,req)
      .subscribe({
          next: ()=>
          {
            this.getCustomers();
            this.showSuccess("Customer: "+this.customer.name+" has been updated");
          }
      });

    }

  }

toggleVisble():void
  {
      this.isVisible2= this.isVisible2===true ? false:true
  }
}
<p-sidebar [(visible)]="isVisible" position="right" styleClass="p-sidebar-lg" [transitionOptions]="'300ms cubic-bezier(0,0,0.2, 1)'">
    <app-manage-customer [customer]="customer" (submit)="saveCustomer($event)"></app-manage-customer>
</p-sidebar>


<p-sidebar  [(visible)]="isVisible2" position="right" styleClass="p-sidebar-lg" [transitionOptions]="'300ms cubic-bezier(0,0,0.2, 1)'">
    <app-update-customer></app-update-customer>
</p-sidebar>


<p-confirmDialog [style]="{width: '50vw'}"></p-confirmDialog>

<p-toast position="bottom-center"></p-toast>

<div class="flex align-items-strectch" style="height: 99vh;">

    <div class="flex flex-column align-items-strectch border-right-1 col-2 border-gray-200">
        <app-menu-bar></app-menu-bar>
    </div>

    <div class="flex flex-column flex-grow-1 ">

        <div>
            <app-header-bar></app-header-bar>
        </div>

        <div class="main-container p-3">
            <button pButton pRipple icon="pi pi-plus" label="Create Customer" class="p-button-info" (click)="isVisible=true"></button>

            <div class="flex justify-content-center align-items-center flex-wrap gap-5 mt-4">
                <app-customer-card *ngFor="let Customer of customers; let index =index" [customer]="Customer"  [customerIndex]="index" (submit)="deleteCustomer($event)" [isVisible2]="isVisible2">
                </app-customer-card>
            </div>
        </div>

    </div>

</div>
